import React, { Component } from 'react';
import { connect } from 'react-redux'
import { editBudget, getBudgets } from '../../actions/budget'

class BudgetEdit extends Component {
  state = {
    description: '',
    amount: '',
    start_date: '',
    end_date: '',
    user_id: 1
  }

  handleChange = (event) => {
    console.log(event.target.name);
    console.log(event.target.value);
    this.setState({ [event.target.name]: event.target.value})
  }
  //
  handleSubmitBudget = (event) => {
    console.log(this.state.selectedBudget);
    event.preventDefault()

    if (this.state.selectedBudget) {
      let budget = { ...this.props.selectedBudget, ...this.state}
      this.props.editBudget(budget)
      event.target.reset()
    } else {
      alert('Please add a description, amount, start date, and/or end date.')
    }
  }

  render() {
    console.log(this.state);
    return (
      <div>

        <form onSubmit={this.handleSubmitBudget}>
          <div>
            <label> Budget Description: </label>
            <input type="text" name="description" onChange={this.handleChange} value={this.props.selectedBudget.description}></input>
            <label> Budget Amount: </label>
            <input type="text" name="amount" onChange={this.handleChange} value={this.props.selectedBudget.amount}></input>
            <div>
              <label> Budget Start Date: </label>
              <input type="date" name="start_date" onChange={this.handleChange} value={this.props.selectedBudget.start_date}></input>
              <label> Budget End Date </label>
              <input type="date" name="end_date" onChange={this.handleChange} value={this.props.selectedBudget.end_date}></input>
            </div>
          </div>

          <div>
            <button> Save </button>
          </div>
        </form>

      </div>
    );
  }
}


const mapStateToProps = state => {
  return {
    budgets: state.budgetReducer.budgets,
    budget: state.budgetReducer.budget
  }
}

const mapDispatchToProps = dispatch => {
  return {
    editBudget: budget => editBudget(budget, dispatch),
    getBudgets: () => getBudgets(dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BudgetEdit)
