import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Doughnut } from 'react-chartjs-2'

class DoughnutChart extends Component {
  // get all expenses for a specific budget through categories

  // go through budgets where it matches the budget.id === category.budget_id === array
  // with that new array go through where it matches category.id === expense.category_id === array

  // mapThroughBudgetsToMatchCategory = () => {
  //     this.props.budgets.map((budget) => {
  //     let cats = this.props.categories.filter((category) => {
  //       return category.budget_id === budget.id
  //     })
  //     console.log(cats);
  //
  //     cats.map((cat) => {
  //       let expense = this.props.expenses.filter((exp) => {
  //         console.log(exp);
  //         return exp.category_id === cat.id
  //       })
  //     })
  //   })
  // }

  render() {
    let data = {
      labels: ['Pink', 'Blue', 'Yellow'],
      datasets: [{
        data: [700, 50, 100],
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56'
        ],
        hoverBackgroundColor: [
          // '#FF6384',
          // '#36A2EB',
          '#FFFFFF'
        ]
      }]
    }

    // {this.mapThroughBudgetsToMatchCategory()}
    if (this.props.budgets.length >0) {

      this.props.budgets[0]((budget) => {
        let cats = this.props.categories.filter((category) => {
          return category.budget_id === budget.id
        })
        // console.log(cats);
        data.labels = cats.map(cat => cat.title)
        return cats.map((cat) => {
          let expense = this.props.expenses.filter((exp) => {
            // console.log(exp);
            return exp.category_id === cat.id
          })

        })
      })
    }

    // console.log(mapThroughBudgetsToMatchCategory);
    console.log(this.props);
    return (
      <div>
        <Doughnut data={data} />
      </div>
    )
  }

}

const mapStateToProps = state => {
  return {
    budgets: state.budgetReducer.budgets,
    categories: state.categoryReducer.categories,
    expenses: state.expenseReducer.expenses
  }
}

export default connect(mapStateToProps)(DoughnutChart)
