import {
  GET_EXPENSES,
  GET_EXPENSE,
  ADD_EXPENSE,
  EDIT_EXPENSE,
  DELETE_EXPENSE,
} from '../constants/action-type'

export const getExpenses = (dispatch) => {
  fetch('http://localhost:3000/expenses')
    .then(response => response.json())
    .then(data => {
      // console.log(data)
      dispatch({ type: GET_EXPENSES, payload: data })
    })
}

export const addExpense = (expense, dispatch) => {
  fetch('http://localhost:3000/expenses', {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(expense)
  })
    .then(response => response.json())
    .then(data => {
      // console.log(data)
      dispatch({ type: ADD_EXPENSE, payload: data })
    })
    getExpenses()
}

export const editExpense = (expense, dispatch) => {
  fetch(`http://localhost:3000/expenses${expense.id}`, {
    method: 'PATCH',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(expense)
  })
    .then(response => response.json())
    .then(data => {
      // console.log(data)
      dispatch({ type: EDIT_EXPENSE, payload: data })
    })
}

export const deleteExpense = (expense, dispatch) => {
  console.log(expense);
  fetch(`http://localhost:3000/expenses/${expense.id}`, {
    method: 'DELETE'
  })
}
