import React from 'react'
import PropTypes from 'prop-types'
import { Doughnut } from 'react-chartjs-2'

const DoughnutChartMaker = (props) => {
  let data = {
    labels: ['Pink', 'Blue', 'Yellow'],
    datasets: [{
      data: [700, 50, 100],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56'
      ],
      hoverBackgroundColor: [
        // '#FF6384',
        // '#36A2EB',
        '#FFFFFF'
      ]
    }]
  }

  if (categories.length === 0) {
    return <div></div>
  } else {
    console.log(props)
    data.labels = props.categories.map(cat => cat.title)
    data.datasets.backgroundColor = props.categories.map(cat => cat.color)
    data.datasets.data = props.categories.map(cat => {
      let expenses = props.expenses.filter(e => e.category_id === cat.id)
      return expenses.length ? expenses.reduce((acc, val) => (acc + val)) : 0
    })
    let arr = []
    for (let i=0; i < props.categories.length; i++) {
      arr.push('#FFFFFF')
    }
    data.datasets.hoverBackgroundColor = arr
    // cats.map((cat) => {
