import React, { Component } from 'react';
import { connect } from 'react-redux'
import { getExpenses, deleteExpense } from '../../actions/expense'

class ExpenseMain extends Component {

  state = {
    expenses: []
  }

  componentDidMount() {
    fetch('http://localhost:3000/expenses')
      .then(response => response.json())
      .then(data => {
        // console.log(this.state)
        // console.log(data)
        this.setState({ ...this.state.expenses, ...this.state.expenses, data})
        // this.setState({this.state.expenses: data})
      })
  }

  mapExpenses = () => {
    console.log(this.state)
    if (this.props.expenses.length > 0) {
      return this.props.expenses.map((expenses) => {
        return expenses.map((expense) => {
          return (
            <tr key={expense.id}>
              <td></td>
              <td>{expense.description}</td>
              <td> {expense.date}</td>
              <td> {expense.amount} </td>
              <td>
                <button> Edit </button>
                <button> Delete </button>
              </td>
            </tr>
          )
        })
      })
    }
  }

  render() {
    // console.log(this.props);
    return (
      <div>

        <div>
          <select>
            <option> Week </option>
            <option> Month </option>
          </select>
        </div>

        <div>

          <table>
            <tbody>
              <tr>
                <th> Budget Title <button> Sort </button></th>
                <th> Expense Dates <button> Sort </button></th>
                <th> Expense Amount <button> Sort </button></th>
                <th> Remaining <button> Sort </button></th>
              </tr>

              <tr>
                <th> Category Color </th>
              </tr>

              {this.mapExpenses()}

              <tr>
                <td></td>
                <td></td>
                <td></td>
                <td> Remaining </td>
              </tr>
            </tbody>
          </table>

        </div>

      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    expenses: state.expenseReducer.expenses
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getExpenses: () => getExpenses(dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseMain)
