import React, { Component } from 'react';
import Main from './Main'
import SignUp from './SignUp'
import Login from './Login'
import Profile from './Profile'
import ProfileEdit from './ProfileEdit'
import { connect } from 'react-redux'
import { editUser } from '../../actions/home'

class Home extends Component {
  state = {
    profileEdit: false
  }

  showSignupLogin = () => {
    if (this.props.signup === true) {
      return <SignUp />
    } else if (this.props.login === true) {
      return <Login />
    }
  }

  handleSubmitEditProfile = (event) => {
    event.preventDefault()
    if (this.state.first_name && this.state.last_name && this.state.email && this.state.password) {
      let user = { ...this.props.user, ...this.state }
      this.props.editUser(user)
      event.target.reset()
    } else {
      alert('Please fill out all information.');
    }
  }

  showEditProfile = () => {
    if (this.state.profileEdit === true) {
      return <ProfileEdit
        profileEdit={this.props.profileEdit}
        handleSubmitEditProfile={this.handleSubmitEditProfile}
      />
    }
  }

  handleClickEditProfile = () => {
    this.setState({profileEdit: !this.state.profileEdit})
  }

  render() {
    return (
      <React.Fragment>
        {localStorage.jwt || this.props.user.first_name
        ?
          <React.Fragment>
            <Profile handleClickEditProfile={this.handleClickEditProfile}/>
            {this.showEditProfile()}
          </React.Fragment>
        :
          <React.Fragment>
            <Main handleClickSignup={this.props.handleClickSignup} handleClickLogin={this.props.handleClickLogin}/>
            {this.showSignupLogin()}
          </React.Fragment>
        }
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.userReducer.user
  }
}

export default connect(mapStateToProps)(Home);
