import React, { Component } from 'react';
import { connect } from 'react-redux'
import { editExpense } from '../../actions/expense'

class ExpenseEdit extends Component {
  state = {
    categories: [],
    category_id: '',
    description: '',
    date: '',
    amount: '',
  }

  componentDidUpdate(prevProps) {
    // console.log(this.prevprops);
    // console.log(this.props);
    if (this.props.selectedExpense !== prevProps.selectedExpense && this.props.selectedExpense) {
       this.setState({
         category_id: '',
         description: '',
         date: '',
         amount: '',
      })
    }
  }

  handleChangeEdit = (event) => {
    // console.log(event.target.value)
    this.setState({ [event.target.name]: event.target.value })
  }

  handleEditExpense = (event) => {
    event.preventDefault()

    if (this.state.category_id && this.state.description && this.state.date && this.state.amount) {
      let expense = { ...this.props.expense, ...this.state}
      this.setState({
        category_id: '',
        description: '',
        date: '',
        amount: '',
      })
      expense = { ...this.props.selectedExpense, ...this.state }
      this.props.addExpense(expense)
      // if (this.props.selectedExpense) {
      //
      //   this.props.editExpense(expense)
      // } else {
      //
      // }
      // event.target.reset()
    } else {
      alert('Please fill out all information.')
    }

  }

  render() {
    // console.log(this.state);
    return (
      <div>
        <div> Expense </div>
        <form onSubmit={this.handleEditExpense}>
          <select name="category_id" onChange={this.handleChangeEdit} value={this.state.category_id}>
            <option value="" disabled selected hidden>Category</option>
            {this.props.categories.map((category) => <option key={category.id} value={category.id}>{category.title}</option>)}
          </select>
          <input type="text" name="description" onChange={this.handleChangeEdit} value={this.state.description}></input>
          <input type="date" name="date" onChange={this.handleChangeEdit} value={this.state.date}></input>
          <input type="text" name="amount" onChange={this.handleChangeEdit} value={this.state.amount}></input>
          <button> Save </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    expenses: state.expenseReducer.expenses,
    categories: state.categoryReducer.categories
  }
}

const mapDispatchToProps = dispatch => {
  return {
    editExpense: expense => editExpense(expense, dispatch),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseEdit)
