import React from 'react'
import PropTypes from 'prop-types'
import { Doughnut } from 'react-chartjs-2'

const DoughnutChartMaker = ({categories, expenses}) => {
  let data = {
    labels: ['Pink', 'Blue', 'Yellow'],
    datasets: [{
      data: [700, 50, 100],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56'
      ],
      hoverBackgroundColor: [
        // '#FF6384',
        // '#36A2EB',
        '#FFFFFF'
      ]
    }]
  }

  if (categories.length === 0) {
    return <div></div>
  }
  console.log(props)
  data.labels = cats.map(cat => cat.title)
  data.datasets.backgroundColor = cats.map(cat => cat.color)
  data.datasets.data = cats.map(cat => {
    let expenses = this.props.expenses.filter(e => e.category_id === cat.id)
    return expenses.length ? expenses.reduce((acc, val) => (acc + val)) : 0
  })
  let arr = []
  for (let i=0; i < cats.length; i++) {
    arr.push('#FFFFFF')
  }
  data.datasets.hoverBackgroundColor = arr
  cats.map((cat) => {
    let expense = this.props.expenses.filter((exp) => {
      // console.log(exp);
      return exp.category_id === cat.id
    })

  })

  return (
    <div>
      <Doughnut data={data} />
    </div>
  )
}

export default DoughnutChartMaker
