import React, { Component } from 'react';
import Navbar from './navbar/Navbar'
import Home from './home/Home'
import Category from './category/Category'
import Budget from './budget/Budget'
import Expense from './expense/Expense'
import WeekMonth from './weekmonth/WeekMonth'
import Year from './year/Year'
import {
  BrowserRouter as Router,
  Route
} from 'react-router-dom'

class BudgeIt extends Component {

  state = {
    date: Date(),
    // home: false,
    // category: false,
    // budget: false,
    // expense: false,
    // weekmonth: false,
    // year: false
  }

date = () => {
  let today = new Date();
  let dd = today.getDate();
  let mm = today.getMonth() + 1; //January is 0!
  let yyyy = today.getFullYear();
  let day= today.getDate()

  if (dd < 10) {
    dd = '0' + dd;
  }
  if (mm < 10) {
    mm = '0' + mm;
  }
  let Newtoday = mm + '/' + dd + '/' + yyyy;

  // return dd
  return day
}

day = () => {
  let days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
  Date.prototype.getDayName = function() {
      return days[ this.getDay() ];
  };
  let now = new Date();
  let day = now.getDayName();
  return day
}

month = () => {
  let months = ['January','February','March','April','May','June','July','August','September','October','November','December'];
  Date.prototype.getMonthName = function() {
      return months[ this.getMonth() ];
  };
  let now = new Date();
  let month = now.getMonthName();
  return month
}


  render() {
    return (
      <Router>
        <div>
          <div> {this.state.date} </div>
          {this.day()}, {this.month()}
          <Navbar />
            <Route exact path="/home" component={Home} />
            <Route exact path="/categories" component={Category} />
            <Route exact path="/budgets" component={Budget} />
            <Route exact path="/expenses" component={Expense} />
            <Route exact path="/weekmonth" component={WeekMonth} />
            <Route exact path="/year" component={Year} />
        </div>
      </Router>
    );
  }
}

export default BudgeIt;
