import { ADD_USER } from "../constants/action-type";

const initialState = {
  user: {}
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_USER:
      return { ...state, user: action.payload };
    default:
      return state;
  }
};
export default rootReducer;
