import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addCategory, editCategory } from '../../actions/category'
import './CategoryEdit.css'

class CategoryEdit extends Component {
  state = {
    description: '',
    color: ''
  }

  handleChange = (event) => {
    // console.log(event.target.value);
    this.setState({ [event.target.name]: event.target.value})
  }

  handleClick = (event) => {
    console.log(event.target.id);
    this.setState({ color: event.target.id})
  }

  handleSubmitCategory = (event) => {
    // console.log(this.props.category);
    event.preventDefault()
    let category = { ...this.props.category, ...this.state}
    // console.log(category);
    this.props.addCategory(category)
    event.target.reset()
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmitCategory}>
          <div>
            <label> Description: </label>
            <input type="text" name="description" onChange={this.handleChange}></input>
            <div id="color_container">
              <div id="color_1" onClick={this.handleClick}> Color </div>
              <div id="color_2" onClick={this.handleClick}> Color </div>
              <div id="color_3" onClick={this.handleClick}> Color </div>
              <div id="color_4" onClick={this.handleClick}> Color </div>
              <div id="color_5" onClick={this.handleClick}> Color </div>
              <div id="color_6" onClick={this.handleClick}> Color </div>
              <div id="color_7" onClick={this.handleClick}> Color </div>
              <div id="color_8" onClick={this.handleClick}> Color </div>
              <div id="color_9" onClick={this.handleClick}> Color </div>
            </div>
            <div>
              <button> Save </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    category: state.categoryReducer.category
  }
}

const mapDispatchToProps = dispatch => {
  return {
    addCategory: category => addCategory(category, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CategoryEdit)
