import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Link,
  Redirect,
  withRouter,
  NavLink
} from 'react-router-dom'
import { connect } from 'react-redux'
import { logout } from '../../actions/home'
import './Navbar.css'

class Navbar extends Component {
  state = {
    show: false,
    bar: false
  }

  dayOfWeek = () => {
    let days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    Date.prototype.getDayName = function() {
      return days[ this.getDay() ];
    };
    let now = new Date();
    let day = now.getDayName();
    return day
  }

  month = () => {
    let months = ['January','February','March','April','May','June','July','August','September','October','November','December'];
    Date.prototype.getMonthName = function() {
      return months[ this.getMonth() ];
    };
    let now = new Date();
    let month = now.getMonthName();
    return month
  }

  day = () => {
    let today = new Date();
    let day = today.getDate();
    return day
  }

  year = () => {
    let today = new Date();
    let year = today.getFullYear();
    return year
  }

  time = () => {
    var time = new Date();
    return time.toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })
  }

  handleClickBarContainer = () => {
    this.setState({bar:!this.state.bar})
  }

  handleClickLink = () => {
    function boxCloser(e){
  if(e.target.id != 'bar_container'){
     document.body.removeEventListener('click', bar, false);
     $('#bar_container').hide();
  }
}
    this.setState({bar:!this.state.bar})
  }

  navBar = () => {
    if (localStorage.jwt && this.props.user.first_name) {
      return (
        <React.Fragment>
          <div className='navbar'>
            <div id="bar_container" onClick={this.handleClickBarContainer}>â˜°</div>
            {this.state.bar
              ?
                <div id="navlink_container">
                  <div className='navoption' onClick={this.handleClickLink}><NavLink to='/home' className='navlink'>My Profile</NavLink></div>
                  <div className='navoption' onClick={this.handleClickLink}><NavLink to='/budgets' className='navlink'>Budget</NavLink></div>
                  <div className='navoption' onClick={this.handleClickLink}><NavLink to='/categories' className='navlink'>Category</NavLink></div>
                  <div className='navoption' onClick={this.handleClickLink}><NavLink to='/expenses' className='navlink'>Expense</NavLink></div>
                  <div className='navoption' onClick={this.handleClickLink}><NavLink to='/home' className='navlink' onClick={this.props.handleClickLogout}>Logout</NavLink> </div>
                </div>
              :
              null
            }
          </div>
        </React.Fragment>
      )
    } else {
      return(
        <React.Fragment>
          <div id='logged_out_navbar'>
            <div id='logged_out_navoption'><NavLink to='/home' id='logged_out_navlink' onClick={this.handleClickLogo}>BudgeIt</NavLink></div>
          </div>
        </React.Fragment>
      )
    }
  }

  render() {
    console.log(this.state.bar)
    return (
      <React.Fragment>

        <div id="header_container">
          <div id="date"> {this.dayOfWeek()} {this.month()} {this.day()}, {this.year()} {this.time()} </div>
        </div>

        {this.navBar()}

      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.userReducer.user
  }
}

const mapDispatchToProps = dispatch => {
  return {
    logout: user => logout(user, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Navbar)
