import { ADD_USER, LOGIN, EDIT_PROFILE } from "../constants/action-type";

const initialState = {
  user: {
    id: '',
    first_name: '',
    last_name: '',
    email: ''
  }
};

const userReducer = (state = initialState, action) => {
  let user
  switch (action.type) {
    case ADD_USER:
      user = action.payload
      return { ...state, user }
    case LOGIN:
      user = action.payload
      return {...state, user }
    case EDIT_PROFILE:
      user = action.payload
      return {...state, user}
    default:
      return state;
  }
}
export default userReducer;
