import { ADD_USER, LOGIN, EDIT } from "../constants/action-type";

const initialState = {
  user: {}
};

const rootReducer = (state = initialState, action) => {
  let user
  switch (action.type) {
    case ADD_USER:
      user = action.payload
      return { ...state, user }
    case LOGIN:
      user = action.payload
      return {...state, user }
    case EDIT:
      user = action.payload

      return {...state, user}
    default:
      return state;
  }
}
export default rootReducer;
