import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addExpense, editExpense } from '../../actions/expense'

class ExpenseEdit extends Component {
  state = {
    expense: [],
    description: '',
    date: '',
    amount: '',
    user_id: 1,
    edit: false,
    submit: false
  }

  handleChange = (event) => {
    // console.log(event.target.value)
    this.setState({ [event.target.name]: event.target.value })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    // console.log(this.props.expense)
    // console.log(this.state)
    if (this.state.description && this.state.date && this.state.amount) {
      let expense = { ...this.props.expense, ...this.state}
      this.props.addExpense(expense)
      event.target.reset()
    } else {
      alert('Please fill out all information.')
    }
  }

  render() {
    // console.log(this.props);
    return (
      <div>
        <div> Expense </div>
        <form onSubmit={this.handleSubmit}>
          <input type="text" name="description" onChange={this.handleChange}></input>
          <input type="date" name="date" onChange={this.handleChange}></input>
          <input type="text" name="amount" onChange={this.handleChange}></input>
          <button> Save </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    expense: state.expenseReducer.expense
  }
}

const mapDispatchToProps = dispatch => {
  return {
    addExpense: expense => addExpense(expense, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseEdit)
