import React, { Component } from 'react';
import CategoryMain from './CategoryMain'
import CategoryAdd from './CategoryAdd'
import { connect } from 'react-redux'

class Category extends Component {
  state = {
    id: '',
    title: '',
    color: ''
  }

  handleChangeCategorySearch = (event) => {
    console.log(event.target.value);
    return this.props.categories.filter((category) => {
      return category.title.toLowerCase().includes(event.target.value.toLowerCase())
    })
  }


  render() {
    // console.log(this.state);
    return (
      <div>
        <CategoryMain handleChangeCategorySearch={this.handleChangeCategorySearch()}/>
        <CategoryAdd />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    category: state.categoryReducer.category
  }
}

const mapDispatchToProps = dispatch => {
  return {
    category: category => category(category, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Category)
