  let data = {
    labels: ['Pink', 'Blue', 'Yellow'],
    datasets: [{
      data: [700, 50, 100],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56'
      ],
      hoverBackgroundColor: [
        // '#FF6384',
        // '#36A2EB',
        '#FFFFFF'
      ]
    }]
  }

  let expenses = props.expenses.filter(e => e.category_id === cat.id)

  if (props.categories.length === 0 || expenses.length === 0) {
    return <div></div>
