import {
  ADD_EXPENSE,
  EDIT_EXPENSE,
  DELETE_EXPENSE,
} from '../constants/action-type'

export const addExpense = (expense, dispatch) => {
  return fetch('https://budgeit-backend.herokuapp.com/expenses', {
    method: 'POST',
    headers: {
      Authorization: `Bearer ${localStorage.getItem('jwt')}`,
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(expense)
  })
    .then(response => response.json())
    .then(data => {
      dispatch({ type: ADD_EXPENSE, payload: data })
    })
}

export const editExpense = (expense, dispatch) => {
  return fetch(`https://budgeit-backend.herokuapp.com/expenses/${expense.id}`, {
    method: 'PATCH',
    headers: {
      Authorization: `Bearer ${localStorage.getItem('jwt')}`,
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(expense)
  })
    .then(response => response.json())
    .then(data => {
      dispatch({ type: EDIT_EXPENSE, payload: data })
    })
}

export const deleteExpense = (expense, dispatch) => {
  return fetch(`https://budgeit-backend.herokuapp.com/expenses/${expense.id}`, {
    method: 'DELETE',
    headers: {
      Authorization: `Bearer ${localStorage.getItem('jwt')}`
    }
  })
    .then(response => response.json())
    .then(data => {
      dispatch({ type: DELETE_EXPENSE, payload: expense })
    })
}
