import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Doughnut } from 'react-chartjs-2'

class DoughnutChart extends Component {
  // get all expenses for a specific budget through categories

  // go through budgets where it matches the budget.id === category.budget_id === array
  // with that new array go through where it matches category.id === expense.category_id === array

  mapThroughBudgetsToMatchCategory = () => {

    let budgetCategory = []
    return this.props.budgets.map((budget) => {
      let cats = this.props.categories.filter((category) => {
        category.budget_id === budget.id
      })
      cats.map((cat) => {
        let expense = this.props.expenses.filter((expense) => {
          expense.category_id === cat.id
        })
      })
    })
  }

  render() {
    let data = {
      labels: ['Pink', 'Blue', 'Yellow'],
      datasets: [{
        data: [700, 50, 100],
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56'
        ],
        hoverBackgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56'
        ]
      }]
    }

    // console.log(this.props.budgets);
    // console.log(this.props.categories);
    // console.log(this.props.expenses);
    return (
      <div>
        <Doughnut data={data} />
      </div>
    )
  }

}

const mapStateToProps = state => {
  return {
    budgets: state.budgetReducer.budgets,
    categories: state.categoryReducer.categories,
    expenses: state.expenseReducer.expenses
  }
}

export default connect(mapStateToProps)(DoughnutChart)
