import React, { Component } from 'react';
import { connect } from 'react-redux'
import { getExpenses, deleteExpense } from '../../actions/expense'

class ExpenseMain extends Component {

  state = {
    expenses: [],
    expense: []
  }

  componentDidMount() {
    this.props.getExpenses()
  }

  handleClickEdit = (expense) => {
    // console.log(expense.id);
    // console.log('clicked');
    this.setState({expense: expense})
    // console.log(this.state);
  }

  handleClickDelete = (expense) => {
    // console.log(expense.id);
    // console.log('clicked');
    this.props.deleteExpense(expense)
  }

  mapExpenses = () => {
    if (this.props.expenses.length > 0) {
      console.log(this.props.expenses)
      return this.props.expenses.map((expenses) => {
        console.log(expenses);
        let newExpenses = expenses
        return newExpenses.map((expense) => {
          console.log(expense);
          return (
            <tr key={expense.id}>
              <td></td>
              <td>{expense.description}</td>
              <td> {expense.date}</td>
              <td> {expense.amount} </td>
              <td>
                <button id={expense.id} onClick={() => this.handleClickEdit(expense)}> Edit </button>
                <button id={expense.id} onClick={() => this.handleClickDelete(expense)}> Delete </button>
              </td>
            </tr>
          )
        })
      })
    }
  }

  render() {
    // console.log(this.props);
    return (
      <div>

        <div>
          <select>
            <option> Week </option>
            <option> Month </option>
          </select>
        </div>

        <div>

          <table>
            <tbody>
              <tr>
                <th> Budget Title <button> Sort </button></th>
                <th> Expense Dates <button> Sort </button></th>
                <th> Expense Amount <button> Sort </button></th>
                <th> Remaining <button> Sort </button></th>
              </tr>

              <tr>
                <th> Category Color </th>
              </tr>


              {this.mapExpenses()}


              <tr>
                <td></td>
                <td></td>
                <td></td>
                <td> Remaining </td>
              </tr>
            </tbody>
          </table>

        </div>

      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    expenses: state.expenseReducer.expenses
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getExpenses: () => getExpenses(dispatch),
    deleteExpense: (expense) => deleteExpense(expense,dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseMain)
